struct ts_stream{	uint8_t stream_type;	uint8_t stream_id;};struct ts_program{	uint16_t program_number;	uint16_t program_map_pid;	uint16_t program_clock_reference;	int program_clock_reference_set;	struct list_element* elementary_streams;};struct transport_stream{	uint16_t network_pid;	uint8_t serial_number;	int network_pid_set;	int serial_number_set;	struct list_element* programs;	};struct list_element {	void* element_data;	struct list_element* next;};